search.queryText:
// Gather/"UNION" all relevant logs
(
  _sourceCategory=prod/app/java
  _sourceName=spectrum-event-listener-srvc
  "Publishing" "type=EODCOMP" "event=EndOfDayEvent(company=1234, opsArea=ACTIVE, currentProcessDate" "status=COMPLETED)"
OR
(
  _sourceCategory=prod/app/java
  _sourceName=creditline-servicing-srvc-async
  "c.c.c.s.t.CyclingTask" "completed run Cycling Task cyclingAsOfDate") 
)
// Parse the `currentProcessDate` (from EndOfDayEvent), which is the new Spectrum clock, from spectrum-event-listener-srvc logs
| parse "Publishing id=*, type=EODCOMP, event=EndOfDayEvent(company=1234, opsArea=ACTIVE, currentProcessDate=*, status=COMPLETED)" AS id, dateFromSpectrum nodrop
// Parse the `cyclingAsOfDate`, from creditline-servicing-srvc
| parse "completed run Cycling Task cyclingAsOfDate=*\"" AS lastQueuedCyclingTaskDate nodrop
// Flatten everything, by taking the last log from each
| merge lastQueuedCyclingTaskDate takeLast, dateFromSpectrum takeLast
// isCyclingUpToDate!
// Note: dateFromSpectrum <=> currentProcessDate - 1 day
// TODO PLC-7034: instead of relying on cycling dates, rely on Spectrum Message IDs
| WHERE lastQueuedCyclingTaskDate != formatDate(toLong(parseDate(dateFromSpectrum, "yyyy-MM-dd") - 86400), "yyyy-MM-dd", "America/Los_Angeles")

_sourceCategory=prod/app/java AND (_sourceName=dosh-rewards-srvc) 
AND "ERROR"
| json auto
| where p="ERROR"
| where (
  // INCLUSION(S)
  (stacktrace matches "*com.credify.doshrewards.external.DoshApiClient*")
  OR (l matches "*.DoshApiClient")
) AND !(
  // EXCLUSION(S)
  (stacktrace matches ("*PrematureCloseException: Connection prematurely closed BEFORE response*")) // PLC-6261
  OR (m matches ("*Too Many Requests*")) // to be removed once dosh fix is done PLC-7157 PLC-7246
)
